import requests
from bs4 import BeautifulSoup

async def pinterest(query):
    url = f'https://id.pinterest.com/search/pins/?autologin=true&q={query}'
    headers = {
        "cookie": "_auth=1; _b=\"AVna7S1p7l1C5I9u0+nR3YzijpvXOPc6d09SyCzO+DcwpersQH36SmGiYfymBKhZcGg=\"; _pinterest_",
        "_ir": "0",
    }

    try:
        response = requests.get(url, headers=headers)
        response.raise_for_status()  # Raise an exception for non-200 status codes

        soup = BeautifulSoup(response.text, "html.parser")
        images = soup.find_all("div", class_="pinImageWrapper")  # Find all the main image containers

        results = []
        for image in images:
            link = image.find("img")["src"]  # Extract the image source
            results.append(link)

        return results

    except requests.exceptions.RequestException as e:
        print(f"Error: {e}")
        return None

# Example usage:
async def main():
    query_result = await pinterest("jojo")
    if query_result:
        print(query_result)
    else:
        print("Failed to fetch Pinterest images.")

if __name__ == "__main__":
    import asyncio

    asyncio.run(main())
